<?php/*|--------------------------------------------------------------------------| Routes File|--------------------------------------------------------------------------|| Here is where you will register all of the routes in an application.| It's a breeze. Simply tell Laravel the URIs it should respond to| and give it the controller to call when that URI is requested.|*/Route::get('/', function () {    return view('welcome');});//Route::get('student/info','StudentController@info');Route::get('student/info', ['uses' => 'StudentController@info']);Route::get('response', ['uses' => 'StudentController@response']);Route::any('student/information', 'StudentController@information');Route::any('request1', ['uses' => 'StudentController@request1']);Route::group(['middleware' => ['web']], function () {    Route::any('session1', ['uses' => 'StudentController@session1']);    Route::any('session2', ['uses' => 'StudentController@session2', 'as' => 'session2']);});Route::any('activity0', 'StudentController@activity0');//活动页面的中间件Route::group(['middleware'=>['activity']],function(){    Route::any('activity1', 'StudentController@activity1');    Route::any('activity2', 'StudentController@activity2');});/*|--------------------------------------------------------------------------| Application Routes|--------------------------------------------------------------------------|| This route group applies the "web" middleware group to every route| it contains. The "web" middleware group is defined in your HTTP| kernel and includes session state, CSRF protection, and more.|*/Route::group(['middleware' => ['web']], function () {    //访问student控制器    Route::get('student/index',['uses'=>'StudentController@index']);    Route::any('student/create',['uses'=>'StudentController@create']);    Route::any('student/save',['uses'=>'StudentController@save']);    Route::any('student/update/{id}',['uses'=>'StudentController@update'])->where('id','[0-9]+');    Route::any('student/delete/{id}',['uses'=>'StudentController@delete'])->where('id','[0-9]+');});